package com.tarnag.hearts;/** * Created by Marci on 2017. 02. 28.. */public class ServerGameThread extends Thread {    public Player[] players=new Player[4];    private GameActivity gameActivity;    private CommunicationServer communicationServer;    ServerGameThread(GameActivity gameActivity,CommunicationServer communicationServer){        this.gameActivity=gameActivity;        this.communicationServer=communicationServer;    }    @Override    public void run() {        beforeDealing();            }    public void beforeDealing(){        for (int i=0;i<4;i++){            for (int j=0;j<4;j++){                send(i,"Player."+Integer.toString(j)+"."+Integer.toString(players[j].score)+"."+players[j].playerName);            }        }    }    private void send(int position, String msg){        communicationServer.sendMessage(position,msg);    }}